    private void jComboBoxContinenteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jComboBoxContinenteActionPerformed
        if(jComboBoxContinente.getSelectedItem().equals("Asia")){
            jComboBoxPais.removeAllItems();
           String paises[]=new String[]{"Japan","China","Russia","South Korea","India"};
            for(int i=0;i<paises.length;i++){
                jComboBoxPais.addItem(paises[i]);    
            } 
        }
        if(jComboBoxContinente.getSelectedItem().equals("America")){
           jComboBoxPais.removeAllItems();
            String paises[]=new String[]{"Mexico","USA","Canada","Brazil","Chile"};
            for(int i=0;i<paises.length;i++){
                jComboBoxPais.addItem(paises[i]);    
            } 
        }
        if(jComboBoxContinente.getSelectedItem().equals("Europa")){
           jComboBoxPais.removeAllItems();
            String paises[]=new String[]{"Germany","Belgium","Croatia","Portugal","Spain"};
            for(int i=0;i<paises.length;i++){
                jComboBoxPais.addItem(paises[i]);    
            } 
        }
        if(jComboBoxContinente.getSelectedItem().equals("Africa")){
           jComboBoxPais.removeAllItems();
            String paises[]=new String[]{"Angola","Cameroon","Congo","South Africa","Egypt"};
            for(int i=0;i<paises.length;i++){
                jComboBoxPais.addItem(paises[i]);    
            } 
        }
        if(jComboBoxContinente.getSelectedItem().equals("Oceania")){
           jComboBoxPais.removeAllItems();
            String paises[]=new String[]{"New Zealand","Australia","Samoa","Nauru","Fiji"};
            for(int i=0;i<paises.length;i++){
                jComboBoxPais.addItem(paises[i]);    
            } 
        }
        
        
        
    }//GEN-LAST:event_jComboBoxContinenteActionPerformed

