    private void jButtonBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonBuscarActionPerformed
       String pais=jComboBoxPais.getItemAt(jComboBoxPais.getSelectedIndex());
       String continente=jComboBoxContinente.getItemAt(jComboBoxContinente.getSelectedIndex());
       String capitalesAsia[]=new String[]{"Tokyo","Beijing","Moscow","Seoul","New","Deli"};
       String capitalesAmerica[]=new String[]{"CDMX","Washington D.C.","Ottawa","Brasilia","Santiago"};
       String capitalesEuropa[]=new String[]{"Berlin","Brussels","Zagreb","Lisbon","Madrid"};
       String capitalesAfrica[]=new String[]{"Luanda","Yaounde","Kinshasa","Pretoria","Cairo"};
       String capitalesOceania[]=new String[]{"Wellington","Canberra","Apia","Yaren","Suva"};
       if(continente.equals("Asia")){
           String capital=capitalesAsia[jComboBoxPais.getSelectedIndex()];
           jLabelTexto.setText("Continent: "+continente+"Country: "+pais+"Capital: "+capital);
       }
       if(continente.equals("Africa")){
           String capital=capitalesAfrica[jComboBoxPais.getSelectedIndex()];
           jLabelTexto.setText("Continent: "+continente+"Country: "+pais+"Capital: "+capital);
       }
       if(continente.equals("America")){
           String capital=capitalesAmerica[jComboBoxPais.getSelectedIndex()];
           jLabelTexto.setText("Continent: "+continente+"Country: "+pais+"Capital: "+capital);
       }
       if(continente.equals("Oceania")){
           String capital=capitalesOceania[jComboBoxPais.getSelectedIndex()];
           jLabelTexto.setText("Continent: "+continente+"Country: "+pais+"Capital: "+capital);
       }
       if(continente.equals("Europa")){
           String capital=capitalesEuropa[jComboBoxPais.getSelectedIndex()];
           jLabelTexto.setText("Continent: "+continente+"Country: "+pais+"Capital: "+capital);
       }
    }//GEN-LAST:event_jButtonBuscarActionPerformed

